package oopsday3.line;

public class PointClass{
	int x;
	int y;
	public PointClass() {
		// TODO Auto-generated constructor stub
	}
	public PointClass(int x, int y) {
		super();
		this.x = x;
		this.y = y;
	}
	public int getX() {
		return x;
	}
	public void setX(int x) {
		this.x = x;
	}
	public int getY() {
		return y;
	}
	public void setY(int y) {
		this.y = y;
	}
	
	

}
